// borrowed from vue-loader
const IS_TEST = !!process.env.VUE_LOADER_TEST
const fs = require('fs')
const path = require('path')

exports.lib = function (file) {
  return path.resolve(__dirname, '../', file)
}

exports.dep = function (dep) {
  if (IS_TEST) {
    return dep
  } else if (fs.existsSync(path.resolve(__dirname, '../../node_modules', dep))) {
    // npm 2 or npm linked
    return 'vue-i18n-loader/node_modules/' + dep
  } else {
    // npm 3
    return dep
  }
}
